import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import {MovieInfo} from '../Components/Movieinfo';
import PropTypes from 'prop-types';


const IMG_API = "https://image.tmdb.org/t/p/w1280";

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"



const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}

const click = event => {
   
    console.log(event.target.value);
  };





const Movie = props=> {

    const [ movies, setMovies, setState] = useState([]);
    const moviedetail = props.datta;
    const imgg = props.datta.poster_path;
    const ttitle = props.datta.title;
    const imbd = props.datta.vote_average;
    return (
        <div className="movie"> 

        <img src={IMG_API + imgg} alt={ttitle}/>
            <div className="movie-info">
            
                <h3>{ttitle}</h3>
                <span className={
                    `tag ${setVoteClass(imbd)}`
                    }>
                {imbd}</span>
                
        
        </div>
        
        <div className="movie-over">
            <h2>Overview:</h2>

            <button onClick={click} className="buttonList">
            <Link to={"/movieinfo/" + moviedetail.id}>More info</Link>
            </button>

            {movies.length > 0 && movies.map((movie) => <Movie datsa={movie.id}/>
          )}
           
        </div> 
        
   
        </div>
    );

}

MovieInfo.propTypes = {
    movie: PropTypes.object
  }
