const Movie = props=> {
    const moviedetail = props.data;
    return (
        <div className="movie"> 

        <img src={IMG_API + moviedetail.poster_path} alt={moviedetail.title}/>
            <div className="movie-info">
            
                <h3>{moviedetail.title}</h3>
                <span className={
                    `tag ${setVoteClass(moviedetail.vote_average)}`
                    }>
                {moviedetail.vote_average}</span>
                
        
        </div>
        
        <div className="movie-over">
            <h2>Overview:</h2>

            <button onClick={click} className="buttonList">
            <Link to={"/movieinfo/" + moviedetail.id}>More info</Link>
            </button>

        </div> 
        
        </div>
    );

}
    export default Movie;
