import '../App.css';
import {Movie} from '../Api/Movie';
import React, {useEffect, useState} from 'react';

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"

const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}
  
const MovieInfo = propss => {

    const [ moviet, setMovies, setState] = useState([]);
    useEffect(() =>{
      getMovies(FEATURED_API, GENRE_API);
    
    },[]);
  
      const getMovies = (API) => {
        fetch(API)
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          setMovies(data.results);
        });
      }
    //const moviedetails = propss.datsa;
    const imggg = propss.datsa.poster_path;
    const tttitle = propss.datsa.title;
    const iimbd = propss.datsa.vote_average;
    return(
        
        <div className="aboutmovie">
              

        <img src={IMG_API + imggg} alt={tttitle}/>
        <div className="movie-info">
        
            <h3>{tttitle}</h3>
            <span className={
                `tag ${setVoteClass(iimbd)}`
                }>
            {iimbd}</span>
        </div>        
       </div>

      
        );
 }
export {MovieInfo};
