import React, {useEffect, useState, useRef} from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import  Movie  from '../Api/Movie';

import image1 from '../plus.png';
import image2 from '../Search.png';
import image3 from '../chat.png';
import image4 from '../user.png';



const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"




export default function Search() {
  const buttonRef = useRef(null);
  const [ movies, setMovies, setState] = useState([]);
  const [searchTerm, setSearchTerm]= useState('');
  
  const [favourites, setFavourites] = useState([]);
  const [MainMovieImage, setMainMovieImage] = useState(null)
  const [Loading, setLoading] = useState(true)
  const [CurrentPage, setCurrentPage] = useState(0)
  useEffect(() =>{
    getMovies(FEATURED_API, GENRE_API);
  
  },[]);

    const getMovies = (API) => {
      fetch(API)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
    }

  const handleOnSubmit= (e) => {
    e.preventDefault();

    if(searchTerm){
      getMovies(SEARCH_API+searchTerm);
    // fetch(SEARCH_API + searchTerm)
    // .then((res) => res.json())
    // .then((data) => {
    //   setMovies(data.results);
    // });

    setSearchTerm(''); 
    }
  };
  
  const handleOnChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const loadMoreItems = () => {
    let endpoint = '';
    setLoading(true)
    console.log('CurrentPage', CurrentPage)
    endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=${CurrentPage + 1}`;
    fetchMovies(endpoint);
  
  }

  return(

        // kolla upp n채r man bara trycker enter utan n책gon text.
      <><header>
          <form onSubmit={handleOnSubmit}>
              <input className="search" type="search" src={image2} placeholder= "Search for movietitle..." value={searchTerm} onChange={handleOnChange} />
          </form>
      </header>
      <div className="movie-container">

      {movies.length > 0 && movies.map((movie, index) => <React.Fragment key={index}><Movie movieId={movie.id} image={movie.poster_path ?
                                    `${IMAGE_API}${movie.poster_path}`
                                    : null} movieName={movie.title}/></React.Fragment>
          )}

          </div>
          {Loading &&
                    <div>Loading...</div>}

                <br />
                <div style={{ display: 'flex', justifyContent: 'center' }}>
                    <button ref={buttonRef} className="loadMore" onClick={loadMoreItems}>Load More</button>
                </div>

          <div className= "extra">
        
        </div>
        
          <div className="Bar">
              <nav>
              <Link to="/"><img className="homepic1" src={image3} alt="home" /></Link>
              <Link to="/Search"><img className="homepic2" src={image2} alt="search" /></Link>
              <Link to="/Recomend"> <img className="homepic3" src={image1} alt="chat" /></Link>
              <Link to="/MyProfile"><img className="homepic4" src={image4} alt="profile" /></Link>
              </nav>

          </div></>

  );

}
