import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';



import Movie from '../Api/Movie';
import image1 from '../Home.png';
import image2 from '../Search.png';
import image3 from '../chat.png';
import image4 from '../profile.png';

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"

export default function Home() {
  

  const [ movies, setMovies, setState] = useState([]);
  const [searchTerm, setSearchTerm]= useState(' ');
  
  useEffect(() =>{
    getMovies(FEATURED_API, GENRE_API);
  
  },[]);

    const getMovies = (API) => {
      fetch(API)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
    }





  return(

    <><div className="movie-container">

          {movies.length > 0 && movies.map((movie) => <Movie key={movie.id} {...movie} />
          )}

      </div><div className="Bar">
              <nav>
              <Link to="/"> <img className="homepic" src={image1} alt="image1" /></Link>
                  <Link to="/Search"><img className="homepic" src={image2} alt="image2" /></Link>  
                  <Link to="/Recomend"><img className="homepic" src={image3} alt="image3" /></Link>
                  <Link to="/MyProfile"><img className="homepic" src={image4} alt="image4" /></Link>

              </nav>
          </div></>

  );

}
