}

const click = event => {
   
    console.log(event.target.value);
  };

export default function Movie (props) {
    const [ movies, setMovies, setState] = useState([]);
    let {key, image, movieId, movieName} = props;
      
        return(
            <>
       
            <div className="movie">

                <img src={IMG_API + image} alt={movieName} />
                <div className="movie-info">
                    <h3>{movieName}</h3>
                    {/* <span className={`tag ${setVoteClass(vote_average)}`}>
                        {vote_average}</span> */}

                </div>
                <div className="movie-over">
                    <h2>Overview:</h2>
                    
                    <button onClick={click} className="buttonList">
                        <a href={`/movieinfo/${movieId}`}>More info
                            
                        {movies.length > 0 && movies.map((movie, index) => <React.Fragment key={index}><Movie movieId={movie.id} image={movie.poster_path ?
                                    `${IMAGE_API}${movie.poster_path}`
                                    : null} movieName={movie.original_title}/></React.Fragment>
          )}
                        </a>
                    </button>
               
                </div>

            </div>
            
           </>
        
        );
      }

