import '../App.css';
import Movie from '../Api/Movie';
import React, {useEffect, useState, useContext} from 'react';
import ReactDOM from "react-dom";
import { fetchMovies, fetchMovieDetail } from '../getdetail';
import Home from './Homepage';
import { GlobalContext } from './GlobalState';

import axios from 'axios';
import {useParams} from "react-router-dom"
import image1 from '../plus.png';
import { Link } from 'react-router-dom';
import { WatchList } from './Myprofile';

//import axios from 'axios';

const IMG_API = 'https://image.tmdb.org/t/p/w1280';

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280/";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"
const API_KEY = '618a4d3048a1a1f797c014affaf110b4';
const API_URL = 'https://api.themoviedb.org/3/';




const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}

export const MovieInfo = ({image, movie, movieName}) =>{

  const [movies, setMovies] = useState([]);
	const [favourites, setFavourites] = useState([]);

  //const {movieId} = useParams();
  const [Movie, setMovie] = useState([])
  const [Casts, setCasts] = useState([])
  const [CommentLists, setCommentLists] = useState([])
  const [LoadingForMovie, setLoadingForMovie] = useState(true)
  const [LoadingForCasts, setLoadingForCasts] = useState(true)
  const [ActorToggle, setActorToggle] = useState(false)
  const movieVariable = {
      movieId: movie  }

  useEffect(() => {

      let endpointForMovieInfo = `${API_URL}movie/${movie}?api_key=${API_KEY}&language=en-US`;
      fetchDetailInfo(endpointForMovieInfo)


  }, [])

  const toggleActorView = () => {
      setActorToggle(!ActorToggle)
  }

  const fetchDetailInfo = (endpoint) => {

      fetch(endpoint)
          .then(result => result.json())
          .then(result => {
              console.log(result)
              setMovie(result)
              setLoadingForMovie(false)

              let endpointForCasts = `${API_URL}movie/${movie}/credits?api_key=${API_KEY}`;
              fetch(endpointForCasts)
                  .then(result => result.json())
                  .then(result => {
                      console.log(result)
                      setCasts(result.cast)
                  })

              setLoadingForCasts(false)
          })
          .catch(error => console.error('Error:', error)
          )
  }
 
  return(
     <div className="background" >
       <img src={IMG_API + '/'+ Movie.poster_path} alt={Movie.title} />
    <div className="aboutmovie">
         

              <img src={IMG_API + '/'+ Movie.poster_path} alt={Movie.title} />
               <div className="movie-info2">

                   <h3>{Movie.title}</h3>
                   <div className="controls">
              <div> 
                <MovieList
                movies={movies}
                handleFavouritesClick={addFavouriteMovie}
                favouriteComponent={AddFavourites}
              />
                 Add to Watchlist
                </div>
            
                   <a href={`/mylist/`}> Mylist </a>
                   <p>{Movie.overview}</p>
                  
                 <span className={`tag ${setVoteClass(Movie.vote_average)}`}>
                
                       {Movie.vote_average}</span>
                       <a href={`/Recomendmovie/${Movie.id}`}><img alt="plus" src={image1} /> </a>
                       
                </div>
                

    </div>
    </div>
    </div>
        
        );
  
}




