import React from "react";
import { MovieControls } from "./MovieControls";
import Movie from "../Api/Movie";
import { useState, useEffect } from "react";
const API_KEY = '618a4d3048a1a1f797c014affaf110b4';
const API_URL = 'https://api.themoviedb.org/3/';
const IMG_API = "https://image.tmdb.org/t/p/w1280";




export const MovieCard = ({ movie, type }) => {

    const [ movies, setMovies, setState] = useState([]);
    useEffect(() => {
        const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
        fetchMovies(endpoint)
    }, [])
    const fetchMovies = (endpoint) => {

        fetch(endpoint)
            .then(result => result.json())
            .then(result => {
                setMovies([...movies, ...result.results])
            },)
            .catch(error => console.error('Error:', error)
            )
      }
  return (
    <div className="movie-card">
      <div className="overlay"></div>

      {movies.length > 0 && movies.map((movie) => <React.Fragment key={movie}><MovieInfo movieId={movie.id} image={movie.poster_path ?
                `${IMG_API}${movie.poster_path}`
             : null} movieName={movie.title}/></React.Fragment>
          )}
    </div>
  );
};
