import React, {useState, useEffect} from 'react';
import { Component } from 'react';
import { Link } from 'react-router-dom';
import {MovieInfo} from '../Components/Movieinfo';
import Home from '../Components/Homepage';
import ReactDOM from "react-dom";

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"



const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}

const click = event => {
   
    console.log(event.target.value);
  };

const Movie = ({title, poster_path, overview, vote_average, id}) =>{

    let movies = this.props.movies.filter(function(movie) {
      }).map(function(movie) {
          return(
              <div key={movie.id} >
              <Link to={'/movieinfo/'+movie.id} ><div info id={movie.id} path={movie.poster_path} title={movie.title} voteAverage={movie.vote_average} release_date={movie.release_date} responsive /></Link>
            </div>
          );
      });
        return(
            <>
           
            <div className="movie">

                <img src={IMG_API + poster_path} alt={title} />
                <div className="movie-info">
                    <h3>{title}</h3>
                    <span className={`tag ${setVoteClass(vote_average)}`}>
                        {vote_average}</span>

                </div>
                <div className="movie-over">
                    <h2>Overview:</h2>
                    
                    <button onClick={click} className="buttonList">
                        <Link to={"/movieinfo/" + id}>More info</Link>
                    </button>
               
                </div>

            </div>
            
           </>
        
        );
      }



    export {Movie};

