import '../App.css';
import {Movie} from '../Api/Movie';
import React, {useEffect, useState} from 'react';
import { renderMatches } from 'react-router';


//import axios from 'axios';

const IMG_API = 'https://image.tmdb.org/t/p/w1280';

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280/";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"
const APIKey = '618a4d3048a1a1f797c014affaf110b4';



const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}


componentDidMount(); {
    this.renderPosts();
  }
const MovieInfo =  async (movieid) => {
    
    try{
        const response = await window.fetch('https://api.themoviedb.org/3/movie/'+movieid+'?api_key=618a4d3048a1a1f797c014affaf110b4');
        const movie = await response.json(); 
        render();{
        return(
            
            <div className="aboutmovie">
        
            <img src={IMG_API + movieid.poster_path}alt={movie.title}/>
            <div className="movie-info2">
            
                <h3>{movie.title}</h3>
                <span className={
                    `tag ${setVoteClass(movie.vote_average)}`
                    }>
                {movie.vote_average}</span>
            </div> 
    
            </div>

        
        );
                }
    
    }
    
    catch (error) {
        console.error(error);
    }
}
    

export {MovieInfo};
