    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}

const click = event => {
   
    console.log(event.target.value);
  };
/*const Movie = ({title, poster_path, overview, vote_average}) =>(


    <div className="movie"> 

        <img src={IMG_API + poster_path} alt={title}/>
            <div className="movie-info">
            
                <h3>{title}</h3>
                <span className={
                    `tag ${setVoteClass(vote_average)}`
                    }>
                {vote_average}</span>
                
        
        </div>
        
        <div className="movie-over">
            <h2>Overview:</h2>

            <button onClick={click} className="buttonList">
            <Link to={"/movieinfo"}>More info</Link>
            </button>

        </div> 
        
        </div>
    );*/




const Movie = props=> {


    const moviedetail = props.datta;
    const imgg = props.datta.poster_path;
    const ttitle = props.datta.title;
    const imbd = props.datta.vote_average;
    return (
        <div className="movie"> 

        <img src={IMG_API + imgg} alt={ttitle}/>
            <div className="movie-info">
            
                <h3>{ttitle}</h3>
                <span className={
                    `tag ${setVoteClass(imbd)}`
                    }>
                {imbd}</span>
                
        
        </div>
        
        <div className="movie-over">
            <h2>Overview:</h2>

            <button onClick={click} className="buttonList">
            <Link to={"/movieinfo/" + moviedetail.id}>More info</Link>
            </button>


           
        </div> 
        
        </div>
    );

}
    export {Movie};
