import "../Css/Prof.css";
import React, {useContext, useState, useEffect} from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import Movie from "../Api/Movie";
import { MovieCard } from "./MovieCard";
import { GlobalContext } from "./GlobalState";
import image1 from '../Home.png';
import image2 from '../Search.png';
import image3 from '../chat.png';
import image4 from '../user.png';
import { MovieInfo } from "./Movieinfo";

//import image from './IMG_6793.jpeg';

const API_KEY = '618a4d3048a1a1f797c014affaf110b4';
const API_URL = 'https://api.themoviedb.org/3/';
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
export const Watchlist = (props) => {
    const {movieId}= useParams();
    const [Movie, setMovie] = useState([])
    useEffect(() => {

        let endpointForMovieInfo = `${API_URL}movie/${movieId}?api_key=${API_KEY}&language=en-US`;
        fetchDetailInfo(endpointForMovieInfo)
  
  
    }, [])
    const fetchDetailInfo = (endpoint) => {

        fetch(endpoint)
            .then(result => result.json())
            .then(result => {
                console.log(result)
                setMovie(result)
                setLoadingForMovie(false)
  
                let endpointForCasts = `${API_URL}movie/${movieId}/credits?api_key=${API_KEY}`;
                fetch(endpointForCasts)
                    .then(result => result.json())
                    .then(result => {
                        console.log(result)
                        setCasts(result.cast)
                    })
  
                setLoadingForCasts(false)
            })
            .catch(error => console.error('Error:', error)
            )
    }
  
    const { watchlist } = useContext(GlobalContext);
    return (
      <div className="movie-page">
        <div className="container">
          <div className="header">
            <h1 className="heading">My Watchlist</h1>
            <span className="count-pill">
              {watchlist.length} {watchlist.length === 1 ? "Movie" : "Movies"}
            </span>
          </div>
  
         
        {watchlist.length > 0 ? (
          <div className="movie-grid">
            {watchlist.map((movie) => (
              <MovieCard movie={movie.id} type={movie} type="watchlist" />
            ))}
          </div>
        ) : (
          <h2 className="no-movies">No movies in your list! Add some!</h2>
        )}
        </div>
      </div>
    );
  };
