import '../App.css';
import {Movie} from '../Api/Movie';
import React, {useEffect, useState} from 'react';
import { fetchMovies, fetchMovieDetail } from '../getdetail';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";


//import axios from 'axios';

const IMG_API = 'https://image.tmdb.org/t/p/w1280';

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280/";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"
const APIKey = '618a4d3048a1a1f797c014affaf110b4';



const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}

const MovieInfo = props =>{
  const handleMovieClick = (id, path) => async (dispatch) => {
    dispatch({ type: MOVIE_CLICKED, payload: id });
  
    let navigationPath = `${path}/${id}/details`;
  
    history.push(navigationPath);
  };
  const { poster_path, title, vote_average, id } = props.movie;
  let runtime;
  if (props.movie.details) {
    runtime = props.movie.details.runtime;
  } else {
    runtime = props.movie.runtime;
  }

  const imageURL = `https://image.tmdb.org/t/p/w780${poster_path}`;

  const convertRuntime = (num) => {
    let hours = num / 60;
    let rhours = Math.floor(hours);
    let minutes = (hours - rhours) * 60;
    let rminutes = Math.round(minutes);
    return rhours + "h " + rminutes + "m";
  };
  
        return(
            <>
              <div>
                      <div
                      src={poster_path = imageURL}
                      onClick={() => props.handleMovieClick(id, path)}
                      alt={`${title} poster`}
                    />
                    {renderRuntime}
                    {renderRating}
                    {props.removeMode && (
                      <div
                        onClick={() => props.removeFavorite(props.movie)}
                      />
                    )}
                  </div>
                );
            <div className="aboutmovie">

                <img src={IMG_API + props.poster_path} alt={props.title} />
                <div className="movie-info2">

                    <h3>{props.title}</h3>
                    <span className={`tag ${setVoteClass(props.vote_average)}`}>
                        {props.vote_average}</span>
                </div>

            </div></>

        
        );
  
}   
export {MovieInfo};
