import '../App.css';
import {Movie} from '../Api/Movie';
import React, {useEffect, useState} from 'react';
import ReactDOM from "react-dom";
import { fetchMovies, fetchMovieDetail } from '../getdetail';
import axios from 'axios';
import { useParams } from 'react-router';


//import axios from 'axios';

const IMG_API = 'https://image.tmdb.org/t/p/w1280';

const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280/";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";
const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=618a4d3048a1a1f797c014affaf110b4&language=en-US"
const APIKey = '618a4d3048a1a1f797c014affaf110b4';



const setVoteClass = (vote) => {
    // if(vote>= 8){
    //     return 'green';
    // }
    // else if (vote>=6){
    //     return 'orange';

    // }else{
    //     return 'red';
    // }
}

export default function MovieInfo(props){
  const movieId = props.match.params.movieId
  //const [Movie, setMovie] = useState([])
  const [Movie, setMovie] = useParams([])
   const [Casts, setCasts] = useState([])
  const [CommentLists, setCommentLists] = useState([])
  const [LoadingForMovie, setLoadingForMovie] = useState(true)
  const [LoadingForCasts, setLoadingForCasts] = useState(true)
  const [ActorToggle, setActorToggle] = useState(false)
  const API_URL = 'https://api.themoviedb.org/3';
  const movieVariable = {
      movieId: movieId
  }

  let { poster_path, title, vote_average} = props
  //const GET_MOVIE = 'https://api.themoviedb.org/3/movie/${movieId}?api_key=618a4d3048a1a1f797c014affaf110b4';
  const [favourites, setFavourites] = useState([]);
  useEffect(() => {

    let endpointForMovieInfo = `${API_URL}/movie/${movieId}?api_key=618a4d3048a1a1f797c014affaf110b4`;
    fetchDetailInfo(endpointForMovieInfo)

    axios.post('/api/comment/getComments', movieVariable)
        .then(response => {
            console.log(response)
            if (response.data.success) {
                console.log('response.data.comments', response.data.comments)
                setCommentLists(response.data.comments)
            } else {
                alert('Failed to get comments Info')
            }
        })

}, [])

const fetchDetailInfo = (endpoint) => {

  fetch(endpoint)
      .then(result => result.json())
      .then(result => {
          console.log(result)
          setMovie(result)
          setLoadingForMovie(false)

          let endpointForCasts = `${API_URL}/movie/${movieId}?api_key=618a4d3048a1a1f797c014affaf110b4`;
          fetch(endpointForCasts)
              .then(result => result.json())
              .then(result => {
                  console.log(result)
                  setCasts(result.cast)
              })

          setLoadingForCasts(false)
      })
      .catch(error => console.error('Error:', error)
      )
}
    
  return(

    <div className="aboutmovie">
          {favourites.map((movie) => ( <Movie data={movie.id} key={movie} {...movie} />))}

              <img src={IMG_API + '/'+  poster_path} alt={title} />
               <div className="movie-info2">

                   <h3>{title}</h3>
                 <span className={`tag ${setVoteClass(vote_average)}`}>
                       {vote_average}</span>
                </div>
    </div>
        
        );
  
}



