import logo from './logo.svg';
import './App.css';
import React, {useEffect, useState} from 'react';
import {getmovie, getpopular} from './Api/api';
import {Search} from './Search';
import MyProfile from './Myprofile';

import Movie from './Api/Movie';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";

import image1 from './Home.png';
import image2 from './Search.png';
import image3 from './chat.png';
import image4 from './profile.png';



const FEATURED_API ="https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=618a4d3048a1a1f797c014affaf110b4&page=1";
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=618a4d3048a1a1f797c014affaf110b4&query=";

export default function App() {


  const [ movies, setMovies, setState] = useState([]);
  const [searchTerm, setSearchTerm]= useState(' ');

  useEffect(() =>{
    getMovies(FEATURED_API);

  },[]);

    const getMovies = (API) => {
      fetch(API)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
    }

  const handleOnSubmit= (e) => {
    e.preventDefault();

    if(searchTerm){
      getMovies(SEARCH_API+searchTerm);
    // fetch(SEARCH_API + searchTerm)
    // .then((res) => res.json())
    // .then((data) => {
    //   setMovies(data.results);
    // });

    setSearchTerm(''); 
    }
  };

  const handleOnChange = (e) => {
    setSearchTerm(e.target.value);
  }

  const openPopup = id => {
    axios( SEARCH_API + "&i=" + id).then(({ data }) => {
      let result = data;

      console.log(result);

      setState(prevState => {
        return { ...prevState, selected: result }
      });
    });
  }

   return (
    <>

        <header>
        <form onSubmit= {handleOnSubmit}>
          <input className="search" type="search" placeholder="Search for movietitle..." value={searchTerm} onChange={handleOnChange} />
          </form>
        </header><div className="movie-container">

          {movies.length > 0 && movies.map((movie) => <Movie key={movie.id} {...movie} />
          )}

        </div>


        <Router>
          <div>
            <div className="Bar">
              <nav>
                <Link to="/">Home   </Link>
                <Link to="/MyProfile">   Profile</Link>
                <Link to="/Search"><img className="homepic" src={image2} alt="image1"/></Link>

              </nav>
              </div>
              <Routes> 
                <Route path ="/Search" element={<Search/>}/>

                <Route path ="/MyProfile" elements={<MyProfile/>}/>
              </Routes>
          </div>
        </Router>


    </>


   );




}



